import { Component } from '@angular/core';

import { CourseService, Course } from '../../services/course.service';

@Component({
  selector: 'app-courses',
  templateUrl: './courses.component.html',
  styleUrls: ['./courses.component.scss'],
  // Add CommonModule to imports
})
export class CoursesComponent {
  courses: Course[] = [];

  constructor(private courseService: CourseService) {}

  ngOnInit() {
    this.courseService.getAllCourses().subscribe({
      next: (data) => (this.courses = data),
      error: (err) => console.error(err),
    });
  }
}
<!DOCTYPE html>
<html lang="en">
<head>
  <meta charset="UTF-8">
  <meta name="viewport" content="width=device-width, initial-scale=1.0">
  <title>Landing Page</title>
  <link href="https://cdn.jsdelivr.net/npm/bootstrap@5.3.0-alpha1/dist/css/bootstrap.min.css" rel="stylesheet">
  <style>
    body {
      font-family: Arial, sans-serif;
      margin: 0;
      padding: 0;
      background-color: #f4f7fa;
    }

    .hero-section {
      background-image: url('http://localhost:9090/images/bridge.png'); /* Replace with your image URL */
      background-size: cover;
      background-position: center;
      background-repeat: no-repeat;
      color: white;
      padding: 80px 20px;
      text-align: center;
      display: flex;
      justify-content: center;
      align-items: center;
      height: 100vh;
    }

    .hero-section h1 {
      font-size: 2.5rem;
      margin-bottom: 20px;
    }

    .hero-section .btn {
      font-size: 1.2rem;
      margin: 10px;
    }

    .header {
      background-color: white;
      padding: 10px 20px;
      display: flex;
      align-items: center;
      justify-content: space-between;
    }

    .header .logo {
      max-width: 120px;
      margin-right: 20px;
    }

    .header .btn {
      margin-left: 20px;
    }

    .courses-section {
      text-align: center;
      margin: 40px 0;
    }

    .courses-section h2 {
      margin-bottom: 30px;
      font-size: 2rem;
      font-weight: bold;
    }

    .card img {
      height: 200px;
      object-fit: cover;
    }

    .card-title {
      font-size: 1.25rem;
    }

    .contact-section {
      background-color: #f8f9fa;
      padding: 40px 20px;
    }

    .contact-section h2 {
      margin-bottom: 20px;
      font-size: 2rem;
      font-weight: bold;
    }

    .register-btn {
      margin: 40px auto;
      text-align: center;
    }

    .register-btn button {
      background-color: #4caf50;
      color: white;
      padding: 15px 30px;
      border: none;
      border-radius: 5px;
      cursor: pointer;
      font-size: 1.2rem;
    }

    .register-btn button:hover {
      background-color: #45a049;
    }

    .container {
      margin: 20px;
    }

    .contact-form input, .contact-form textarea {
      border-radius: 10px;
      padding: 12px;
      font-size: 1rem;
      margin-bottom: 15px;
      border: 1px solid #ccc;
      width: 100%;
    }

    .contact-form button {
      background-color: #007bff;
      color: white;
      padding: 12px 30px;
      font-size: 1.2rem;
      border: none;
      border-radius: 5px;
      cursor: pointer;
    }

    .contact-form button:hover {
      background-color: #0056b3;
    }
    
    .card-body {
      padding: 20px;
      text-align: center;
    }
  </style>
</head>
<body>

  <!-- Header Section with Logo -->
  <div class="header">
    <img src="http://localhost:9090/images/logo.png" alt="Logo" class="logo">
    <div>
      <button class="btn btn-secondary">Login</button>
    </div>
  </div>

  <!-- Hero Section -->
  <div class="hero-section">
    <div class="container text-center">
      <h1>Improve your skills on your own<br>to prepare for a better future</h1>
      <button class="btn btn-primary">View More</button>
      <button class="btn btn-outline-light">Discover Our Courses</button>
    </div>
  </div>

  <!-- Courses Section -->
  <div class="courses-section">
    <h2>Our Courses</h2>
    <div class="container">
      <div class="row justify-content-center">
        <!-- Course Cards -->
        <div class="col-md-4 mb-4">
          <div class="card">
            <img src="http://via.placeholder.com/300x200" class="card-img-top" alt="Course Image">
            <div class="card-body">
              <h5 class="card-title">Course 1</h5>
              <p class="card-text">50 DT/Month</p>
            </div>
          </div>
        </div>
        <div class="col-md-4 mb-4">
          <div class="card">
            <img src="http://via.placeholder.com/300x200" class="card-img-top" alt="Course Image">
            <div class="card-body">
              <h5 class="card-title">Course 2</h5>
              <p class="card-text">60 DT/Month</p>
            </div>
          </div>
        </div>
        <div class="col-md-4 mb-4">
          <div class="card">
            <img src="http://via.placeholder.com/300x200" class="card-img-top" alt="Course Image">
            <div class="card-body">
              <h5 class="card-title">Course 3</h5>
              <p class="card-text">70 DT/Month</p>
            </div>
          </div>
        </div>
      </div>
    </div>
  </div>

  <!-- Contact Section -->
  <div class="contact-section">
    <div class="container">
      <h2>Contact Us</h2>
      <form class="contact-form">
        <div class="mb-3">
          <label for="name" class="form-label">Name</label>
          <input type="text" class="form-control" id="name" placeholder="Your Name">
        </div>
        <div class="mb-3">
          <label for="email" class="form-label">Email</label>
          <input type="email" class="form-control" id="email" placeholder="Your Email">
        </div>
        <div class="mb-3">
          <label for="message" class="form-label">Message</label>
          <textarea class="form-control" id="message" rows="4" placeholder="Write your message here"></textarea>
        </div>
        <button type="submit" class="btn btn-primary">Send the Message</button>
      </form>
    </div>
  </div>

  <!-- Register Button -->
  <div class="register-btn">
    <button class="btn btn-success btn-lg">REGISTER NOW</button>
  </div>

  <script src="https://cdn.jsdelivr.net/npm/bootstrap@5.3.0-alpha1/dist/js/bootstrap.bundle.min.js"></script>
</body>
</html>
